import React, {useEffect, useState} from "react";
import GoogleLogin from "react-google-login";
import { gapi } from "gapi-script";
import Button from 'react-bootstrap/Button';
import "./loginform.css"

const LoginForm = () => {

    useEffect(() => {
        function start() {
            gapi.client.init({
                clientId: "79474543031-tmjo35916ufn421ej3u1i2ljao2apr4s.apps.googleusercontent.com",
                scope: ""
            })
        }
        gapi.load('client: auth2', start)
    })

    const [popupStyle, showPopup] = useState("hide")

    const popup = () => {
        showPopup("login-popup")
        setTimeout(() => showPopup("hide"), 3000)
    }

    const onSuccess = e => {
        alert("User signed in")
        console.log(e)
    }

    const onFailure = e => {
        alert("User sign in Failed")
        console.log(e)
    }

    return (
        <div className='container-fluid d-flex align-items-center justify-content-center mt-5'>
      <div className='row'>
        <div className='col col-xl-10'>
          <form className='rounded bg-white shadow p-5' method='POST'>
            <h5 style={{display:'flex',justifyContent:'center' , textAlign:'center',marginTop:'0%', fontWeight:'bold', color:'#6f60cc'}}>Welcome to Bardeen</h5>
            <div className="mb-3">
              <input style={{display:'flex', justifyContent:'center',width:'20em'}} placeholder="Email address" type="email" className="form-control" id="exampleInputEmail1" />
            </div>
            <div className="mb-3">  
              <input style={{width:'20em'}}  placeholder="password" type="password" className="form-control" id="exampleInputPassword1"/>
            </div>
            <Button className="btn btn-default w-40 my-4 mx-0 buttons" type="submit">
                  Sign In
                </Button>

            <Button className="google btn-default w-40 my-4 mx-0 buttons" type="submit">
                  
                </Button>

            <Button className="github btn-default w-40 my-4 mx-0 buttons" type="submit">
                  Github
                </Button>
          </form>
        </div>
      </div>
    </div>
    )
}

export default LoginForm
